function Map(c,a,b){this.MapOptions={zoom:b,center:new google.maps.LatLng(c,a),mapTypeId:google.maps.MapTypeId.ROADMAP};this.init=function(d){this.map=new google.maps.Map(document.getElementById(d),this.MapOptions)};this.changeType=function(d){if(d==="default"){this.map.setMapTypeId(google.maps.MapTypeId.ROADMAP)}if(d==="satellite"){this.map.setMapTypeId(google.maps.MapTypeId.SATELLITE)}if(d==="hybrid"){this.map.setMapTypeId(google.maps.MapTypeId.HYBRID)}if(d==="terrain"){this.map.setMapTypeId(google.maps.MapTypeId.TERRAIN)}};this.addMarker=function(f){var d=new google.maps.Marker({position:new google.maps.LatLng(f.lat,f.lng),map:this.map,draggable:f.draggable});if(f.html!==undefined){var e=new google.maps.InfoWindow({content:f.html,maxWidth:200});google.maps.event.addListener(d,"click",function(){e.open(this.map,d)})}}};